package com.demo;import java.io.*;/** * @program: AutoTestDemo * @description: 反序列化测试，验证serialVersionUID作用，运行时判断类的serialVersionUID来验证版本一致性的 * @author: gonghuihui * @create: 2018-09-21 10:02 **/public class DeserializationTest implements Serializable {    public static void main(String[] args) throws IOException, ClassNotFoundException {        ObjectInputStream ois1 = new ObjectInputStream(new FileInputStream("worm.out"));        String s1 = (String)ois1.readObject();        Worm w1 = (Worm)ois1.readObject();        ois1.close();        System.out.println("反序列化操作1之后");        System.out.println(s1);        System.out.println("w1: " + w1);    }}