package com.demo;import java.io.*;/** * @program: AutoTestDemo * @description: ExternalizableTest * @author: gonghuihui * @create: 2018-10-08 19:55 **/public class ExternalizableTest {    public static void main(String[] args) throws IOException, ClassNotFoundException {        System.out.println("序列化之前");        Blip b = new Blip("This String is " , 47);        System.out.println(b);        System.out.println("序列化操作，writeObject");        ByteArrayOutputStream out = new ByteArrayOutputStream();        ObjectOutputStream oos = new ObjectOutputStream(out);        oos.writeObject(b);        System.out.println("反序列化之后,readObject");        ByteArrayInputStream in = new ByteArrayInputStream(out.toByteArray());        ObjectInputStream ois = new ObjectInputStream(in);        Blip bb = (Blip)ois.readObject();        System.out.println(bb);    }}