package com.demo;import com.demo.classes.Animal;import com.demo.classes.House;import java.io.*;/** * @program: AutoTestDemo * @description: * @author: gonghuihui * @create: 2018-09-21 13:56 **/public class MyWorld {    public static void main(String[] args) throws IOException, ClassNotFoundException {        House house = new House();        System.out.println("序列化前");        Animal animal = new Animal("test", house);        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();        ObjectOutputStream oos = new ObjectOutputStream(outputStream);        oos.writeObject(animal);        oos.flush();        oos.close();        System.out.println("反序列化后");        ByteArrayInputStream inputStream = new ByteArrayInputStream(outputStream.toByteArray());        ObjectInputStream ois = new ObjectInputStream(inputStream);        Animal animal1 = (Animal)ois.readObject();        ois.close();        }    }