package com.demo;import java.io.Console;import java.io.PrintWriter;/** * @program: AutoTestDemo * @description: TestConsole简单使用，idea不能用了，是不是废弃了 * @author: gonghuihui * @create: 2018-10-11 10:39 **/public class TestConsole {    public static void main(String[] args) {        Console console = System.console();        if (console != null) {            PrintWriter printWriter = console.writer();            printWriter.write("input:");            console.flush();            String str1 = console.readLine();            console.format("%s", str1);        }    }}