package com.http.api.testcases;import com.github.kevinsawicki.http.HttpRequest;import org.testng.annotations.Test;/** * @program: AutoTestDemo * @description: http-request库的使用 * @author: gonghuihui * @create: 2018-09-18 19:40 **/public class TestHttpGet {    /*    获取额度所有输出接口    1/贷前用户：2002000011，FinalLimit ！= 0,摘要信息不变，有效期范围内，不触发（pass）    2/贷前用户：2002000011，FinalLimit  ！= 0,摘要信息变化，有效期范围内，触发（pass）    3/贷前用户：2002000011，FinalLimit  ！= 0,摘要信息不变，不在有效期范围内，触发（pass）    4/贷前用户：2002000011，FinalLimit  ！= 0,摘要信息变化，不在有效期范围内，触发（pass）    5/贷前用户：2003100603，FinalLimit = 0,摘要信息不变  （pass）    6/贷前用户：2003100603，FinalLimit = 0,摘要信息改变，触发（pass）    7/贷中用户：2002000007，FinalLimit = 20000,摘要信息不变，有效期范围内，不触发（pass）    8/贷中用户：2002000007，FinalLimit = 20000,摘要信息变化，有效期范围内，触发（pass）     */    @Test    public void testLimit() {       String resp = HttpRequest.get("http://risk-gateway.test.91gfd.cn/risk/limit/get/2002000011").body();       System.out.println("获取额度所有输出接口: " + resp);    }    /*    获取具体额度值     */    @Test    public void testSpecificLimit() {        String resp = HttpRequest.get("http://risk-gateway.test.91gfd.cn/risk/limit/get/2003005479/2}").body();        System.out.println("获取具体额度值: " + resp);    }    /*    获取所有资料有效时间     */    @Test    public void testGetAllData() {        String resp = HttpRequest.get("http://risk-gateway.test.91gfd.cn/risk/limit/get/times/2003005479").body();        System.out.println("获取所有资料有效时间: " + resp);    }    /*    获取某一资料项的失效时间     */    @Test    public void testGetDisableTime() {        String resp = HttpRequest.get("http://risk-gateway.test.91gfd.cn/risk/limit/get/2003005479/2}").body();        System.out.println("获取某一资料项的失效时间: " + resp);    }}