package com.http.api.utils;import org.apache.commons.lang.StringUtils;import javax.servlet.http.Cookie;import javax.servlet.http.HttpServletRequest;/** * @program: HttpAutoTest * @description: 获取cookies * @author: gonghuihui * @create: 2018-09-12 14:10 **/public class SessionUtils {    public static String getCookie(HttpServletRequest request, String cookieName) {        Cookie[] cookies = request.getCookies();        if (null == cookies || cookies.length == 0) {            return null;        }        for (Cookie c : cookies) {            if (StringUtils.equals(cookieName, c.getName())) {                return c.getValue();            }        }        return null;    }}